import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import ProfileModal from './ProfileModal';

function Header() {
    const [studentInfo, setStudentInfo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const navigate = useNavigate(); // Initialize navigate hook

    useEffect(() => {
        fetchStudentInfo();
    }, []);

    const fetchStudentInfo = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/student-info', {
                withCredentials: true,
            });
            console.log(response);
            if (response.data && response.data.length > 0) {
                setStudentInfo(response.data[0]);
            }
        } catch (error) {
            console.error('Error fetching student info:', error);
        }
    };

    const handleEditProfile = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const handleUpdateSuccess = () => {
        fetchStudentInfo();
    };

    const handleLogout = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/logout', {
                withCredentials: true,
            });
            if (response.data.success) {
                alert('Logout successful');
                navigate('/login'); // Redirect to the login page after successful logout
            }
        } catch (error) {
            console.error('Error logging out:', error);
        }
    };

    return (
        <header className="header">
            <h1>Tumo Task</h1>
            {studentInfo ? (
                <div className="student-info">
                    <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                    <div className="buttons-container">
                        <button onClick={handleEditProfile}>Edit Profile</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                </div>
            ) : (
                <div className="student-info">
                    <span>Loading...</span>
                </div>
            )}
            {isModalOpen && (
                <ProfileModal isOpen={isModalOpen} onClose={handleCloseModal} initialData={studentInfo} onUpdateSuccess={handleUpdateSuccess} />
            )}
        </header>
    );
}

export default Header;
