import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentList() {
    const [students, setStudents] = useState([]);
    const [searchFirstName, setSearchFirstName] = useState('');
    const [searchFullName, setSearchFullName] = useState('');

    useEffect(() => {
        fetchStudents();
    }, [searchFirstName, searchFullName]); // Re-fetch when search criteria change

    const fetchStudents = async () => {
        try {
            let url = 'http://localhost:8000/api/students';

            // Append query params for search if provided
            if (searchFirstName || searchFullName) {
                url += `?first_name=${encodeURIComponent(searchFirstName)}&last_name=${encodeURIComponent(searchFullName)}`;
            }

            const response = await axios.get(url, {
                withCredentials: true,
            });
            setStudents(response.data);
        } catch (error) {
            console.error('Error fetching students:', error);
        }
    };

    const handleSearch = () => {
        // Trigger fetchStudents again to apply search filters
        fetchStudents();
    };

    return (
        <div className="student-list">
            <h2>Students List</h2>
            <div className="search-box">
                <input
                    type="text"
                    placeholder="Search by First Name"
                    value={searchFirstName}
                    onChange={(e) => setSearchFirstName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Search by Full Name (optional)"
                    value={searchFullName}
                    onChange={(e) => setSearchFullName(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>
            <table className="student-table">
                <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Mail</th>
                    <th>Date</th>
                </tr>
                </thead>
                <tbody>
                {students.map((student) => (
                    <tr key={student.user_id}>
                        <td>{student.first_name}</td>
                        <td>{student.last_name}</td>
                        <td>{student.mail}</td>
                        <td>{student.date}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default StudentList;
