// Header.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import ProfileModal from './ProfileModal';

function Header() {
    const [studentInfo, setStudentInfo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchStudentInfo = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/student-info', {
                    withCredentials: true,
                });
                setStudentInfo(response.data[0]); // Assuming response is an array with one object
            } catch (error) {
                console.error('Error fetching student info:', error);
            }
        };

        fetchStudentInfo();
    }, []);

    const handleEditProfile = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <header className="header">
            <div className="header-content">
                <h1>Tumo Task</h1>
                {studentInfo && (
                    <div className="student-info">
                        <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                        <button onClick={handleEditProfile}>Edit Profile</button>
                    </div>
                )}
            </div>
            <ProfileModal isOpen={isModalOpen} onClose={handleCloseModal} initialData={studentInfo} />
        </header>
    );
}

export default Header;
