import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './style.css';
import { Link } from 'react-router-dom';

function Register() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [countries, setCountries] = useState([]);
    const [selectedCountry, setSelectedCountry] = useState('');
    const [cities, setCities] = useState([]);
    const [selectedCity, setSelectedCity] = useState('');

    useEffect(() => {
        const fetchCountries = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/countries');
                setCountries(response.data); // Assuming response.data is an array of countries
            } catch (error) {
                console.error('Error fetching countries:', error);
            }
        };

        fetchCountries();
    }, []);


    useEffect(() => {
        const fetchCities = async () => {
            try {
                console.log(66)
                const response = await axios.post('http://localhost:8000/api/cities', {
                    country: selectedCountry
                });
                setCities(response.data);
            } catch (error) {
                console.error(`Error fetching cities for country ${selectedCountry}:`, error);
            }
        };
    }, [selectedCountry]);


    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/api/register', {
                email, password, firstName, lastName, age, city_id: selectedCity
            });
            alert(response.data.message); // Display success message
        } catch (error) {
            alert('Registration failed: ' + error.message); // Display error message
        }
    };

    const handleCountryChange = (e) => {
        const countryId = e.target.value;
        setSelectedCountry(countryId);
        setCities([]);
    };

    // Render component
    return (
        <div className="register-container">
            <form onSubmit={handleSubmit} className="register-form">
                <h2>Register</h2>
                <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)}
                       required/>
                <input type="password" placeholder="Password" value={password}
                       onChange={(e) => setPassword(e.target.value)} required/>
                <input type="text" placeholder="First Name" value={firstName}
                       onChange={(e) => setFirstName(e.target.value)} required/>
                <input type="text" placeholder="Last Name" value={lastName}
                       onChange={(e) => setLastName(e.target.value)} required/>
                <input type="number" placeholder="Age" value={age} onChange={(e) => setAge(e.target.value)} required/>

                <select value={selectedCountry} onChange={handleCountryChange}>
                    {Array.isArray(countries) && countries.length > 0 ? (
                        countries.map(country => (
                            <option key={country.country_id} value={country.country_id}>
                                {country.country_name}
                            </option>
                        ))
                    ) : (
                        <option value="">No countries available</option>
                    )}
                </select>

                <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>

                    <option value="">Select City</option>
                    {console.log(cities)}
                    {cities.map(city => (
                        <option key={city.city_id} value={city.city_id}>{city.city_name}</option>
                    ))}
                </select>

                <button type="submit">Register</button>
                <p className="register-text">Already have an account? <Link to="/login"
                                                                            className="login-link">Login</Link></p>
            </form>
        </div>
    );
}

export default Register;
