import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProfileModal from './ProfileModal';

function Header({ user_id }) {
    const [studentInfo, setStudentInfo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        if (user_id) {
            fetchStudentInfo(user_id);
        }
    }, [user_id]);

    const fetchStudentInfo = async (userId) => {
        try {
            const response = await axios.post('http://localhost:8000/api/student-info', { user_id: userId }, {
                withCredentials: true,
            });
            if (response.data && response.data.length > 0) {
                setStudentInfo(response.data[0]); // Assuming response is an array with one object
            }
        } catch (error) {
            console.error('Error fetching student info:', error);
        }
    };

    // Function to handle logout
    const handleLogout = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/logout', {}, {
                withCredentials: true,
            });
            console.log(response.data); // Assuming backend returns a success message
            // Clear local state or perform any necessary cleanup
            setStudentInfo(null);
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    return (
        <header className="header">
            <div className="header-content">
                <h1>Tumo Task</h1>
                {studentInfo ? (
                    <div className="student-info">
                        <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div className="student-info">
                        <span>Loading...</span> {/* Show loading indicator */}
                    </div>
                )}
            </div>
            {isModalOpen && (
                <ProfileModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} initialData={studentInfo} />
            )}
        </header>
    );
}

export default Header;
