import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Check if user is already authenticated
        axios.get('http://localhost:8000/api/student-info', { withCredentials: true })
            .then(response => {
                if (response.data) {
                    navigate('/'); // Redirect to home page if logged in
                }
            })
            .catch(error => {
                // Error handling if needed
                console.error('Error checking authentication status:', error);
            });
    }, [navigate]);

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/login', { username, password })
            .then(response => {
                if (response.data.success) {
                    alert('Login successful');
                    navigate('/'); // Redirect to the home page after login
                } else {
                    setErrorMessage('Login failed: Invalid credentials');
                }
            })
            .catch(error => {
                setErrorMessage('Login failed: ' + error.message);
            });
    }

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
                <h2>Login</h2>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        className="form-input"
                    />
                </div>
                <button type="submit" className="login-button">Login</button>
                <p className="register-text">Don't have an account? <Link to="/register" className="register-link">Sign up</Link></p>
            </form>
        </div>
    );
}

export default Login;
