// ProfileModal.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProfileModal({ isOpen, onClose, initialData, onUpdateSuccess }) {
    const [formData, setFormData] = useState(initialData);

    useEffect(() => {
        setFormData(initialData);
    }, [initialData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/api/update-student-info', formData, {
                withCredentials: true,
            });
            console.log(response.data);
            if (response.data.success) {
                onUpdateSuccess(); // Fetch updated data after successful update
                onClose(); // Close modal
            } else {
                // Handle error from server if needed
            }
        } catch (error) {
            console.error('Error updating profile:', error);
            // Handle network or other errors
        }
    };

    return (
        <div className={`modal ${isOpen ? 'open' : ''}`}>
            <div className="modal-content">
                <span className="close-btn" onClick={onClose}>&times;</span>
                <h2>Edit Profile</h2>
                <form onSubmit={handleSubmit}>
                    <label>First Name:
                        <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} />
                    </label>
                    <label>Last Name:
                        <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} />
                    </label>
                    <label>Age:
                        <input type="number" name="age" value={formData.age} onChange={handleChange} />
                    </label>
                    {/* Add other fields as needed */}
                    <button type="submit">Save Changes</button>
                </form>
            </div>
        </div>
    );
}

export default ProfileModal;
