import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProfileModal from './ProfileModal';
import { useNavigate } from 'react-router-dom';
import Login from "./Login";
function Header() {
    const [studentInfo, setStudentInfo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const navigate = useNavigate(); // Initialize navigate for navigation

    useEffect(() => {
        fetchStudentInfo();
    }, []);


    const fetchStudentInfo = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/student-info`, {
                params: { user_id }
            });
            console.log(response);
            if (response.data && response.data.length > 0) {
                setStudentInfo(response.data[0]);
            }
        } catch (error) {
            console.error('Error fetching student info:', error);
        }
    };

    const handleEditProfile = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const handleUpdateSuccess = () => {
        fetchStudentInfo();
    };

    const handleLogout = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/logout', {}, {
                withCredentials: true,
            });
            if (response.data.success) {
                navigate('/login');
            } else {
                console.error('Logout failed');
            }
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    return (
        <header className="header">
            <div className="header-content">
                <h1>Tumo Task</h1>
                {studentInfo ? (
                    <div className="student-info">
                        <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                        <button onClick={handleEditProfile}>Edit Profile</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div className="student-info">
                        <span>Loading...</span>
                    </div>
                )}
            </div>
            {isModalOpen && (
                <ProfileModal isOpen={isModalOpen} onClose={handleCloseModal} initialData={studentInfo} onUpdateSuccess={handleUpdateSuccess} />
            )}
        </header>
    );
}

export default Header;
