import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProfileModal from './ProfileModal';

function Header() {
    const [studentInfo, setStudentInfo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        fetchStudentInfo();
    }, []);

    const fetchStudentInfo = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/student-info', {
                withCredentials: true,
            });
            if (response.data && response.data.length > 0) {
                setStudentInfo(response.data[0]); // Assuming response is an array with one object
            }
        } catch (error) {
            console.error('Error fetching student info:', error);
        }
    };

    const handleEditProfile = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const handleUpdateSuccess = () => {
        fetchStudentInfo(); // Fetch updated student info after successful update
    };

    const handleLogout = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/logout', null, {
                withCredentials: true,
            });
            if (response.data.result) {
                // Logout successful
                setStudentInfo(null); // Clear student info state
            }
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    return (
        <header className="header">
            <div className="header-content">
                <h1>Tumo Task</h1>
                {studentInfo ? (
                    <div className="student-info">
                        <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                        <button onClick={handleEditProfile}>Edit Profile</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div className="student-info">
                        <span>Loading...</span> {/* Show loading indicator */}
                    </div>
                )}
            </div>
            {isModalOpen && (
                <ProfileModal isOpen={isModalOpen} onClose={handleCloseModal} initialData={studentInfo} onUpdateSuccess={handleUpdateSuccess} />
            )}
        </header>
    );
}

export default Header;
