import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './style.css';
import { Link } from 'react-router-dom';
import CityCountryService from '../../engine/project/CityCountryService.php'; // Import the service for fetching city/country data

function Register() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [countries, setCountries] = useState([]);
    const [selectedCountry, setSelectedCountry] = useState('');



    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/http://localhost:8000/api/register', {
                email, password, firstName, lastName, age, city_id: selectedCity
            });
            alert(response.data.message);
        } catch (error) {
            alert('Registration failed: ' + error.message);
        }
    }





    return (
        <div className="register-container">
            <form onSubmit={handleSubmit} className="register-form">
                <h2>Register</h2>
                <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                <input type="text" placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                <input type="text" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                <input type="number" placeholder="Age" value={age} onChange={(e) => setAge(e.target.value)} required />

               

                <button type="submit">Register</button>
                <p className="register-text">Already have an account? <Link to="/login" className="login-link">Login</Link></p>
            </form>
        </div>
    );
}

export default Register;
