// Header.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Header() {
    const [studentInfo, setStudentInfo] = useState(null);

    useEffect(() => {
        const fetchStudentInfo = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/student-info', {
                    withCredentials: true, // Ensure credentials are sent with the request
                });
                setStudentInfo(response.data[0]); // Assuming response is an array with one object
            } catch (error) {
                console.error('Error fetching student info:', error);
            }
        };

        fetchStudentInfo();
    }, []); // Empty dependency array ensures it runs only once

    const handleLogout = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/logout', {}, {
                withCredentials: true, // Ensure credentials are sent with the request
            });
            console.log(response.data); // Assuming the backend returns a success message
            setStudentInfo(null); // Clear studentInfo state after logout
        } catch (error) {
            console.error('Error logging out:', error);
        }
    };

    return (
        <header className="header">
            <div className="header-content">
                <h1>Tumo Task</h1>
                {studentInfo && (
                    <div className="student-info">
                        <span>{studentInfo.first_name} {studentInfo.last_name}</span>
                        <Link to="/edit-student">Edit Profile</Link>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                )}
            </div>
        </header>
    );
}

export default Header;
